import React, { Component } from 'react';



class List extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      companies: [],
      notfound:false,
      search: false,
      keywords : [],
      list_kw : false,
      card : false,
      company : {}
    };        
  this.kwords()
  }



  inputkeyword(name){
    if(name!= ""){
      if(this.state.companies.length == 0) {
        this.setState({
          notfound: true});}
    fetch(`http://localhost:3001/players/list/search/${name}`)
      .then(response => response.json())
      .then(responseJson => {
        this.setState({ companies: responseJson.data });
          if(this.state.companies.length != 0) {
        this.setState({ notfound: false});}
        else {
          this.setState({ notfound: true});}
      });
      this.setState({
        search: true
      });
    }
  };


  kwords(){
    fetch("http://localhost:3001/players/keywords")
    .then(response => response.json())
    .then(responseJson => {
      this.setState({ keywords: responseJson.data });
      this.setState({ list_kw: true });
    });
}

showCard(id){
  fetch(`http://localhost:3001/players/list/${id}`)
    .then(response => response.json())
    .then(responseJson => {
      this.setState({ company: responseJson.data, 
        card: true,
        list_kw : false,
        search : false, });
    });
};

  render(){
    return <div className="container">
  <input type="text"
  id="rechercher"
  className="input"
  placeholder="Search..." />
  <div onClick={() => this.inputkeyword(document.getElementById("rechercher").value)} className="btn btn-primary">
  Search
  </div>
  {this.state.notfound ? (
    <h5> Aucun résultat </h5>
  ): null}
  {this.state.search ? (
    <div className="list-group">
      <h5> Résultat de recherche pour {document.getElementById("rechercher").value} </h5> 
      {this.state.companies.map(companies => (
    <li
      onClick={() => this.showCard(companies._id)}
      className="list-group-item list-group-item-action"
    >
      {companies.name}
      </li>
    ))}
  </div>
): null}
{this.state.list_kw ? (  
  <div className="list-group">
  {this.state.keywords.map(keywords => (
<li
  onClick={() => (document.getElementById("rechercher").value = keywords, this.inputkeyword(keywords))}
  className="btn btn-secondary"
>
  {keywords}
  </li>
    ))} 
  </div>
  ): null}
{this.state.card ? (
  <div class="card" style={{ width: "18rem" }}>
    <div class="card-body">
      <h5 class="card-title">{this.state.company.name}</h5>
      <p class="card-text">{this.state.company.keywords}</p>
      <div onClick={() => this.setState({ card: false,
        list_kw : true,
        search : true, })} class="btn btn-primary">
        Back
      </div>
    </div>
  </div>
) : null}
</div>
  }
}

//reste à faire : lier les noms d'entreprise aux présentations , lier les clics sur mot clés à la recherche avec ce mot-clé

export default List;
