import React, { Component } from 'react';



class List extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      companies: [],
      search: false,
      notfound:false
    };
  }



  inputkeyword(name){
      this.setState({
        notfound: false});
    if(name!= ""){
    fetch(`http://localhost:3001/players/list/search/${name}`)
      .then(response => response.json())
      .then(responseJson => {
        this.setState({ companies: responseJson.data });
      });
      if(this.state.companies.length == 0) {
        this.setState({
          notfound: true});
      }
      this.setState({
        search: true
      });
    }
  };


  render(){
    return <div className="container">
  <input type="text"
  id="rechercher"
  className="input"
  placeholder="Search..." />
  <div onClick={() => this.inputkeyword(document.getElementById("rechercher").value)} className="btn btn-primary">
  Search
  </div>
  {this.state.search ? (
    <div className="list-group">
      {this.state.companies.map(companies => (
    <li
      className="list-group-item list-group-item-action"
    >
      {companies.name}
      </li>
    ))}
  </div>
): null}
  {this.state.notfound ? (
    <h5> Aucun r√©sultat </h5>
    ): null}
    </div>
  }
}



export default List;
