import React, { Component } from 'react';



class List extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      companies: [],
      search: false
    };
  }



  inputkeyword(name){
    fetch(`http://localhost:3001/players/list/search/${name}`)
      .then(response => response.json())
      .then(responseJson => {
        this.setState({ companies: responseJson.data });
      });
      this.setState({
        search: true
      });
  };

  render(){
    return <div className="container">
  <input type="text"
  id="rechercher"
  className="input"
  placeholder="Search..." />
  <a onClick={() => this.inputkeyword(document.getElementById("rechercher").value)} className="bouton1">Search</a>
  {this.state.search ? (
  <div class="card" style={{ width: "18rem" }}>
    <div class="card-body">
      <h5 class="card-title">{this.state.companies}</h5>
    </div>
  </div>
) : null}
</div>
  }
}



export default List;
